  const [entries, setEntries] = useState([]);

  // let entries = [];
  function passData(name, age) {
    entries.push({
      name: name,
      age: age,
    });
    // setEntries(entries);
    console.log("In App.js");
    console.log(entries);
  }

  return (
    <div>
      <Entry onDataSubmit={passData} />
      <InputForm users={entries} />
    </div>
  );
}

export default App;
