  const [entries, setEntries] = useState([]);
  // let entries = [];
  function passData(name, age) {
    entries.push({
      name: name,
      age: age,
    });
    console.log("In App.js");
    console.log(entries);
  }
  return (
    <div>
      <Entry onDataSubmit={passData}></Entry>
      {entries.length == 0 && <p>NOthing to see...</p>}
      {!entries.length == 0 && <p>soMeThIng to see...</p>}
      <InputForm sendEntries={entries}></InputForm>
    </div>
  );
}

export default App;
